(2, 'article-show', '文章显示布局', '两列的文章显示布局', '<div id="header">\n   {{ Page.element(''snippet'',''header'') }}\n</div>\n<div id="navigation">\n   {{ Page.element(''snippet'',''navigation'') }}\n</div>\n<div id="mainbox" class="container">\n	<div id="main-content" class="row-fluid">\n		<div id="main-content-data" class="span8">\n			<h1 class="text-center">{{ Page.entry(''title'') }}</h1>\n			<hr />\n			<div class="text-center">\n				发布时间：{{ Page.entry(''date_created'') | date(''Y-m-d H:i'') }}\n			</div>\n			<hr />\n			{{ Page.element_area(1,''Main Column'') }}\n		</div>\n		<div id="main-content-sidebar" class="span4">\n			{{ Page.element_area(2,''Side Column'') }}\n			<!-- UY BEGIN -->\n			<div id="social-comment-wrapper" data-spy="affix" data-offset-top="80">\n				<div id="uyan_frame"></div>\n				<script type="text/javascript" src="http://v2.uyan.cc/code/uyan.js?uid=1738661"></script>\n			</div>\n			<!-- UY END -->\n		</div>\n	</div>\n</div>\n<div id="footer">\n   {{ Page.element(''snippet'',''footer'') }}\n</div>', NULL, 1398845969),
(4, 'blank', '空白页面', '空白页面空白页面空白页面', '<div id="header">\n   {{ Page.element(''snippet'',''header'') }}\n</div>\n<div id="navigation">\n   {{ Page.element(''snippet'',''navigation'') }}\n</div>\n<div id="mainbox" class="container">\n	<div id="main-content" class="row-fluid">\n		<div class="span12">\n			{{ Page.content() }}\n		</div>\n	</div>\n</div>\n<div id="footer">\n   {{ Page.element(''snippet'',''footer'') }}\n</div>', NULL, 1399477692),
(6, 'category-show', '文章列表布局', '只显示下级文章，也就是只有一个列表啦', '<div id="header">\n   {{ Page.element(''snippet'',''header'') }}\n</div>\n<div id="navigation">\n   {{ Page.element(''snippet'',''navigation'') }}\n</div>\n<div id="mainbox" class="container">\n	<div id="main-content" class="row-fluid">\n		<div id="main-content-category-left" class="span3">\n			{{ Page.element_area(4, ''Left'') }}\n		</div>\n		<div id="main-content-category" class="span6">\n			{{ Page.element_area(6, ''Main Top'') }}\n			<ul id="main-content-category-list" class="nav">\n			{% set i = 0 %}\n			{% for item in Page.nav(''sort=desc'', FALSE) %}\n				{% if i == 0 %}\n				<li class="text-center title"><strong>{{ item.name }}</strong></li>\n				{% else %}\n				<li data-id="{{ item.id }}">\n					<a href="{{ url_site(item.url) }}">\n						{{ item.name }}\n						<span class="pull-right muted">{{ item.date_created | date(''Y-m-d H:i'') }}</span>\n					</a>\n				</li>\n				{% endif %}\n				{% set i = i+1 %}\n			{% endfor %}\n			</ul>\n			{{ Page.element_area(3, ''Main Bottom'') }}\n		</div>\n		<div id="main-content-category-left" class="span3">\n			{{ Page.element_area(5, ''Right'') }}\n		</div>\n	</div>\n</div>\n<div id="footer">\n   {{ Page.element(''snippet'',''footer'') }}\n</div>', 1390640104, 1399477792);

